import { View, Text, SafeAreaView, Image, TextInput, ScrollView} from 'react-native'
import React, { useLayoutEffect } from 'react'
import { useNavigation } from '@react-navigation/native'
import {UserIcon, ChevronDownIcon,SearchIcon, AdjustmentsIcon, AdjustmentsVerticalIcon} from "react-native-heroicons/outline"
import { SearchBar } from 'react-native-screens'
import Categories from '../components/Categories'




const HomeScreen = () => {
  const navigation = useNavigation();

  useLayoutEffect(() => {
      navigation.setOptions({
        headerShown: false,
      })
  }, [])
  return (
    <SafeAreaView className='bg-white pt-5'>

        {/* Header */}
      <View className="flex-row pb-3 items-center mx-4 space-x-2 px-4">
        <Image
            source={{
              uri: 'https://links.papareact.com/wru'
            }}
            className='h-7 w-7 bg-gray-300 p-4 rounded-full'
         />
         <View className='flex-1'>
          <Text className="font-bold text-gray-400 text-xs">
            Deliver Now!
          </Text>
          <Text className= "font-bold text-xl">
            Current Location
            <ChevronDownIcon size={20} color="#00CC88" />
          </Text>
         </View>
         <UserIcon size={35} color= "#00CC88" />
      </View>
                {/* Search */}
            <View className='flex-row items-center space-x-2 pb-2 mx-4' >
              <View className="flex-row flex-1 space-x-2 bg-gray-200 p-3">
                <SearchBar color='gray' size={20}/>
                <TextInput 
                placeholder='Restaurants and cuisines'
                keyboardType='default'
                />
              </View>
              <AdjustmentsVerticalIcon color='#00CC88'/>
            </View>
            {/* Body */}
            <ScrollView
              className='bg-gray-100'
              contentContainerStyle={{
                paddingBottom: 100,
              }}

            >
              {/* Categories */}
               <Categories /> 

              {/* Featured Rows */}
            </ScrollView>


    </SafeAreaView>
  )
}

export default HomeScreen
